import React, { useContext } from 'react';
import { FileContext, AllProductsContext } from '../pages/main';

function ValidateButton() {
  const { selectedFile } = useContext(FileContext);
  const { allProducts } = useContext(AllProductsContext);

  const handleValidation = () => {
    if (!selectedFile) {
      alert('Você deve fazer upload de um arquivo CSV antes de validar.');
      return;
    }

    const newProducts = [];
    const reader = new FileReader();

    reader.onload = (event) => {
      const csvData = event.target.result;
      const allLines = csvData.split('\n').map(line => line.replace(/^"|"$/g, '').replace(/"\r?$/, ''));

      for (let i = 1; i < allLines.length; i++) {
        const line = allLines[i];
        const values = line.split(',');
        const newPrice = parseFloat(values[1]);
        const productCode = parseInt(values[0]);
        const productExists = allProducts.some(product => product.code === productCode);
    
        if (isNaN(productCode) || isNaN(newPrice)) {
          alert('Você deve fornecer um arquivo CSV com ambos os campos numéricos, separados por vírgula. (Exemplo: 17,25.50)');
          return;
        }

        if (!productExists) {
          alert(`O código do produto ${productCode} não está presente na lista de produtos.`);
          return
        }

        const prevProduct = allProducts.find(product => product.code === productCode);
        const newProduct = {
          code: productCode,
          name: prevProduct.name,
          oldPrice: prevProduct.sales_price,
          newPrice: newPrice
        };
        newProducts.push(newProduct);
      }
    };
    reader.readAsText(selectedFile);
  };

  return (
    <div>
      <div>
        <ul>
          {newProducts.map((product, index) => (
            <li key={index}>
              <strong>Código:</strong> {product.code}<br />
              <strong>Nome:</strong> {product.name}<br />
              <strong>Preço Antigo:</strong> R$ {product.oldPrice}<br />
              <strong>Novo Preço:</strong> R$ {product.newPrice}<br />
            </li>
          ))}
        </ul>
      </div>
      <button onClick={handleValidation}>Validar</button>
    </div>
  );
}

export default ValidateButton;
