import React, { useContext } from 'react';
import { FileContext } from '../pages/main';

function ValidateButton() {
  const { selectedFile } = useContext(FileContext);

  const handleValidation = () => {
    if (!selectedFile) {
      alert('Você deve fazer upload de um arquivo CSV antes de validar.');
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      const csvData = event.target.result;
      const lines = csvData.split('\n');
      console.log(lines);
      lines.slice(1).forEach((line, index) => {
        const values = line.split(',');
        const productCode = values[0];
        const newPrice = values[1];

        if (isNaN(productCode) || isNaN(newPrice)) {
          alert(`Linha: ${index + 1}: Todos os produtos devem possuir "product_code" e "new_price" válidos.`);
          return;
        }
      });
    };
    reader.readAsText(selectedFile);
  };

  return (
    <div>
      <button onClick={handleValidation}>Validar</button>
    </div>
  );
}

export default ValidateButton;
